// PokemonList.js
import React, { useState, useEffect } from 'react';
import PokemonCard from './PokemonCard';

function PokemonList({ currentPage, onPageChange, onPokemonClick }) {
  const [pokemonList, setPokemonList] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchPokemon = async () => {
      try {
        setLoading(true);
        const response = await fetch(`https://pokeapi.co/api/v2/pokemon?offset=${(currentPage - 1) * 20}&limit=20`);
        if (!response.ok) {
          throw new Error('Failed to fetch Pokemon');
        }
        const data = await response.json();
        setPokemonList(data.results);
      } catch (error) {
        console.error('Error fetching Pokemon:', error);
      } finally {
        setLoading(false);
      }
    };

    fetchPokemon();
  }, [currentPage]);

  

  return (
    <div className="pokemon-list">
      {loading ? (
        <div>Loading...</div> ) 
        : (
        pokemonList.map((pokemon) => (
          <div key={pokemon.url} className="pokemon-card-wrapper">
            <div className="pokemon-type" style={{ backgroundColor: getTypeColor(pokemon.type) }}>
              {pokemon.type}
            </div>
            <PokemonCard pokemon={pokemon} onClick={() => onPokemonClick(pokemon.url)} />
          </div>
        ))
      )}
    </div>
  );
}

export default PokemonList;
