import React, { useState } from 'react';
import PokemonList from './components/PokemonList';
import PokemonDetails from './PokemonDetails';
import Pagination from './Pagination';

function App() {
  const [selectedPokemonUrl, setSelectedPokemonUrl] = useState(null);
  const [currentPage, setCurrentPage] = useState(1);
  const [totalPages, setTotalPages] = useState(1);

  const handlePokemonClick = (pokemonUrl) => {
    setSelectedPokemonUrl(pokemonUrl);
  };

  const handlePageChange = (page) => {
    setCurrentPage(page);
    // Fetch Pokemon for the new page here
  };

  return (
    <div className="App">
      <h1>Pokedex</h1>
      <PokemonList currentPage={currentPage} onPageChange={handlePageChange} onPokemonClick={handlePokemonClick} />
      <PokemonDetails pokemonUrl={selectedPokemonUrl} />
      <Pagination currentPage={currentPage} totalPages={totalPages} onPageChange={handlePageChange} />
    </div>
  );
}

export default App;
