import React, { useState, useEffect } from 'react';
import PokemonCard from './PokemonCard';
import Pagination from './Pagination';
import { getTypeColor } from './utils';

function PokemonList({ currentPage, onPokemonClick }) {
  const [pokemonList, setPokemonList] = useState([]);
  const [loading, setLoading] = useState(true);
  const [totalPages, setTotalPages] = useState(1);

  const handlePageChange = async (page) => {
    setCurrentPage(page);
    const data = await fetchPokemon(page);
    setTotalPages(Math.ceil(data.count / 20));
  };

  useEffect(() => {
    const fetchPokemon = async () => {
      try {
        setLoading(true);
        const response = await fetch(`https://pokeapi.co/api/v2/pokemon?offset=${(currentPage - 1) * 24}&limit=24`);
        if (!response.ok) {
          throw new Error('Failed to fetch Pokemon');
        }
        const data = await response.json();
        const pokemonDetailsPromises = data.results.map((pokemon) => fetchPokemonDetails(pokemon.url));
        const pokemonDetails = await Promise.all(pokemonDetailsPromises);
        setPokemonList(pokemonDetails);
      } catch (error) {
        console.error('Error fetching Pokemon:', error);
      } finally {
        setLoading(false);
      }
    };

    fetchPokemon();
  }, [currentPage]);

  const fetchPokemonDetails = async (url) => {
    try {
      const response = await fetch(url);
      if (!response.ok) {
        throw new Error('Failed to fetch Pokemon details');
      }
      const data = await response.json();
      return data;
    } catch (error) {
      console.error('Error fetching Pokemon details:', error);
    }
  };

  return (
    <div className="pokemon-list">
    {/* Check if pokemonList is defined before mapping */}
    {pokemonList && pokemonList.length > 0 ? (
      <div className="pokemon-cards-container">
        {pokemonList.map((pokemon) => (
          <div key={pokemon.id} className="pokemon-card-wrapper">
            {/* Pass onPokemonClick handler to PokemonCard */}
            <PokemonCard pokemon={pokemon} onClick={() => onPokemonClick(pokemon.url)} />
          </div>
        ))}
      </div>
    ) : (
      <div>No Pokemon found.</div>
    )}
    <Pagination currentPage={currentPage} totalPages={totalPages} onPageChange={handlePageChange} />
  </div>
  
  );
}

export default PokemonList;

