import './App.css'
import React, { useEffect, useState } from 'react';
import { BrowserRouter as Router, Routes, Route, useLocation } from 'react-router-dom';
import PokemonList from './components/PokemonList';
import Pagination from './components/Pagination';
import PokemonDetailsPage from './Pages/PokemonDetailsPage';

function App() {
  const location = useLocation;
  const isListPage = location.pathname === '/';

  const [selectedPokemonUrl, setSelectedPokemonUrl] = useState(null);
  const [currentPage, setCurrentPage] = useState(1);
  const [totalPages, setTotalPages] = useState(1);
  const [loading, setLoading] = useState(false);

  const handlePokemonClick = (pokemonUrl) => {
    setSelectedPokemonUrl(pokemonUrl);
  };

  const fetchPokemon = async (page) => {
    try {
      setLoading(true);
      const response = await fetch(`https://pokeapi.co/api/v2/pokemon?offset=${(page - 1) * 20}&limit=20`);
      if (!response.ok) {
        throw new Error('Failed to fetch Pokemon');
      }
      const data = await response.json();
      return data;
    } catch (error) {
      console.error('Error fetching Pokemon:', error);
    } finally {
      setLoading(false);
    }
  };

  const handlePageChange = async (page) => {
    setCurrentPage(page);
    const data = await fetchPokemon(page);
    setTotalPages(Math.ceil(data.count / 20));
  };

  useEffect(() => {
    handlePageChange(currentPage);
  }, []);

  return (
    <Router>
      <div className="App">
        <h1>Pokedex</h1>
        <Routes>
          <Route path="/" element={<PokemonList onPageChange={handlePageChange} onPokemonClick={handlePokemonClick} loading={loading} />} />
          <Route path="/pokemon/:id" element={<PokemonDetailsPage />} />
        </Routes>
        <div>
          Curremt path: {location.pathname === ''}
         <Pagination currentPage={currentPage} totalPages={totalPages} onPageChange={handlePageChange} />
         </div>
      </div>
    </Router>
  );
}

export default App;



