// PokemonList.js

import React, { useState, useEffect } from 'react';
import PokemonCard from './PokemonCard';
import { getTypeColor } from './utils';


function PokemonList({ currentPage, onPageChange, onPokemonClick }) {
  const [pokemonList, setPokemonList] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchPokemon = async () => {
      try {
        setLoading(true);
        const response = await fetch(`https://pokeapi.co/api/v2/pokemon?offset=${(currentPage - 1) * 20}&limit=20`);
        if (!response.ok) {
          throw new Error('Failed to fetch Pokemon');
        }
        const data = await response.json();
        const pokemonDetailsPromises = data.results.map((pokemon) => fetchPokemonDetails(pokemon.url));
        const pokemonDetails = await Promise.all(pokemonDetailsPromises);
        
        setPokemonList(pokemonDetails);
      } catch (error) {
        console.error('Error fetching Pokemon:', error);
      } finally {
        setLoading(false);
      }
    };

    fetchPokemon();
  }, [currentPage]);

  const fetchPokemonDetails = async (url) => {
    try {
      const response = await fetch(url);
      if (!response.ok) {
        throw new Error('Failed to fetch Pokemon details');
      }
      const data = await response.json();
      return data;
    } catch (error) {
      console.error('Error fetching Pokemon details:', error);
    }
  };

  return (
    <div className="pokemon-list">
      {loading ? (
        <div>Loading...</div>
      ) : (
        <div className="pokemon-cards-container">
          {pokemonList.map((pokemon) => (
            <div key={pokemon.id} className="pokemon-card-wrapper">
              <PokemonCard pokemon={pokemon} onClick={() => onPokemonClick(pokemon.url)} />
            </div>
          ))}
        </div>
      )}
    </div>
  );
}

export default PokemonList;


